import axios from "axios";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router";
import {
	bookDataFetch,
	cartDataSearchRequest,
	cartDataSearchSuccess,
} from "../redux/actions";
import { SearchBook } from "./SearchBook";

export const BookStore = () => {
	const filterData = useSelector((store) => store.filterData);
	const loading = useSelector((store) => store.loading);
	const dispatch = useDispatch();
	const [cookies, setCookies] = useCookies("");
	const navigate = useNavigate();
	const cartCount = useSelector((store) => store.cartCount);

	useEffect(() => {
		dispatch(bookDataFetch());
	}, [dispatch]);

	const handleClick = (e) => {
		if (cookies.loginToken !== undefined) {
			axios
				.post("http://localhost:3001/cartUpdate", {
					user_id: jwtDecode(cookies.loginToken).user._id,
					book_id: e.target.id,
				})
				.then((res) => {
					console.log(res.status);
				})
				.catch((error) => console.log(error.message.split(" ").pop()));
		}
		if (cookies.loginToken === undefined) {
			dispatch(cartDataSearchRequest());
			dispatch(cartDataSearchSuccess([]));
			alert("login first");
			navigate("/login");
		}
	};

	return (
		<>
			<SearchBook />
			{loading ? (
				<h2>Loading...</h2>
			) : (
				<table>
					<thead>
						<tr>
							<th>Book Name</th>
							<th>Author</th>
							<th>Price</th>
							<th>Action</th>
						</tr>
					</thead>
					<tbody>
						{filterData.length === 0 ? (
							<tr>
								<h3>No Data Found</h3>
							</tr>
						) : (
							filterData.map((book) => {
								return (
									<tr key={book._id}>
										<td>{book.name}</td>
										<td>{book.author}</td>
										<td>₹{book.price}</td>
										<td>
											<button id={book._id} onClick={handleClick}>
												Add to cart
											</button>
										</td>
									</tr>
								);
							})
						)}
					</tbody>
				</table>
			)}
		</>
	);
};
