import { BOOK_DATA_FETCH_REQUEST } from "./actionTypes";

const initState = {
	booksData: [],
	loading: true,
	error: false,
};

export const reducer = (store = initState, { type, payload }) => {
	switch (type) {
		case BOOK_DATA_FETCH_REQUEST:
			return { ...store, loading: true };
		default:
			return store;
	}
};
