import { useCookies } from "react-cookie";
import { useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import axios from "axios";

export const NavBar = () => {
	const [cartLen, setCartLen] = useState(0);
	const [loading, setLoading] = useState(true);
	const [cookies, setCookie, removeCookie] = useCookies("");

	useEffect(() => {
		if (cookies.loginToken !== undefined) {
			axios
				.post("http://localhost:3001/cartFind", {
					user_id: jwtDecode(cookies.loginToken).user._id,
				})
				.then((res) => {
					setCartLen(res.data.book_ids.length);
					setLoading(false);
				});
		} else {
			setCartLen(0);
			setLoading(false);
		}
	}, [loading, cartLen, cookies]);

	return (
		<>
			{cookies.loginToken !== undefined ? (
				<div>{jwtDecode(cookies.loginToken).user.first_name}</div>
			) : (
				<></>
			)}
			<div className="navbar">
				<NavLink to="/">Home</NavLink>
				{cookies.loginToken === undefined ? (
					<NavLink to="/login">Login</NavLink>
				) : (
					<NavLink
						to="/"
						onClick={() => {
							removeCookie("loginToken", cookies.loginToken, { path: "/" });
						}}
					>
						Logout
					</NavLink>
				)}
				{cookies.loginToken === undefined ? (
					<NavLink to="/signup">Signup</NavLink>
				) : (
					<></>
				)}
				{loading === true ? (
					<h4>Loading...</h4>
				) : (
					<NavLink to="/cart">Cart-{cartLen}</NavLink>
				)}
			</div>
		</>
	);
};
