import axios from "axios";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { useDispatch, useSelector } from "react-redux";
import { cartDataSearchRequest, cartDataSearchSuccess } from "../redux/actions";

export const Cart = () => {
	const [cookies, setCookies] = useCookies("");
	const cartData = useSelector((store) => store.cartData);
	const dispatch = useDispatch();

	useEffect(() => {
		console.log(cookies.loginToken);
		if (cookies.loginToken !== undefined) {
			axios
				.post("http://localhost:3001/cartFind", {
					user_id: jwtDecode(cookies.loginToken).user._id,
				})
				.then((res) => {
					res.data.book_ids.map((book) => {
						axios
							.post("http://localhost:3001", { book_id: book })
							.then((res) => {
								dispatch(cartDataSearchRequest());
								dispatch(cartDataSearchSuccess(res.data));
							});
					});
				});
		} else {
			dispatch(cartDataSearchRequest());
			dispatch(cartDataSearchSuccess([]));
		}
	}, [cookies, dispatch]);

	console.log(cartData, cookies.loginToken);

	return (
		<div>
			{cartData.map((cart) => {
				return (
					<div>
						<p>{cart.name}</p>
						<p>{cart.author}</p>
						<p>{cart.price}</p>
					</div>
				);
			})}
		</div>
	);
};
