import { useCookies } from "react-cookie";
import { useDispatch, useSelector } from "react-redux";
import { NavLink } from "react-router-dom";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import axios from "axios";

export const NavBar = () => {
	const cartLen = useSelector((store) => store.cartCount);
	const [cookies, setCookie, removeCookie] = useCookies("");
	const dispatch = useDispatch();

	useEffect(() => {
		if (cookies.loginToken !== undefined) {
			axios
				.post("http://localhost:3001/cartFind", {
					user_id: jwtDecode(cookies.loginToken).user._id,
				})
				.then((res) => {
					dis(res.data.book_ids.length);
				});
		} else {
			setCartLen(0);
		}
	}, [cookies]);

	return (
		<>
			{cookies.loginToken !== undefined ? (
				<div>{jwtDecode(cookies.loginToken).user.first_name}</div>
			) : (
				<></>
			)}
			<div className="navbar">
				<NavLink to="/">Home</NavLink>
				{cookies.loginToken === undefined ? (
					<NavLink to="/login">Login</NavLink>
				) : (
					<NavLink
						to="/"
						onClick={() => {
							removeCookie("loginToken", cookies.loginToken, { path: "/" });
						}}
					>
						Logout
					</NavLink>
				)}
				{cookies.loginToken === undefined ? (
					<NavLink to="/signup">Signup</NavLink>
				) : (
					<></>
				)}
				<NavLink to="/cart">Cart-{cartLen}</NavLink>
			</div>
		</>
	);
};
