import axios from "axios";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { useDispatch, useSelector } from "react-redux";
import { cartDataFetchRequest, cartDataFetchSuccess } from "../redux/actions";

export const Cart = () => {
	const [cookies, setCookies] = useCookies("");
	const cartData = useSelector((store) => store.cartData);
	const loading = useSelector((store) => store.loading);
	const dispatch = useDispatch();

	useEffect(() => {
		if (cookies.loginToken !== undefined) {
			axios
				.post("http://localhost:3001/cartFind", {
					user_id: jwtDecode(cookies.loginToken).user._id,
				})
				.then((res) => {
					res.data.book_ids.map((book) => {
						axios
							.post("http://localhost:3001", { book_id: book })
							.then((res) => {
								dispatch(cartDataFetchRequest());
								dispatch(cartDataFetchSuccess(res.data));
							});
					});
				});
		}
		if (cookies.loginToken === undefined) {
			dispatch(cartDataFetchRequest());
			dispatch(cartDataFetchSuccess([]));
		}
	}, [cookies, dispatch]);

	return loading ? (
		<h3>Loadong...</h3>
	) : (
		<table>
			<thead>
				<tr>
					<th>Book Name</th>
					<th>Author</th>
					<th>Price</th>
				</tr>
			</thead>
			{cartData.length === 0 ? (
				<h3>Empty Cart</h3>
			) : (
				cartData.map((cart) => {
					return (
						<div>
							<p>{cart.name}</p>
							<p>{cart.author}</p>
							<p>{cart.price}</p>
						</div>
					);
				})
			)}
		</table>
	);
};
