import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { useDispatch, useSelector } from "react-redux";
import { bookDataFetch } from "../redux/actions";
import { SearchBook } from "./SearchBook";

export const BookStore = () => {
	const filterData = useSelector((store) => store.filterData);
	const loading = useSelector((store) => store.loading);
	const dispatch = useDispatch();
	const [cookies, setCookies] = useCookies("");

	useEffect(() => {
		dispatch(bookDataFetch());
	}, [dispatch]);

	console.log(cookies.loginToken);

	return (
		<>
			<SearchBook />
			{loading ? (
				<h2>Loading...</h2>
			) : (
				<table>
					<thead>
						<tr>
							<th>Book Name</th>
							<th>Author</th>
							<th>Price</th>
							<th>Action</th>
						</tr>
					</thead>
					<tbody>
						{filterData.length === 0 ? (
							<tr>
								<h3>No Data Found</h3>
							</tr>
						) : (
							filterData.map((book) => {
								return (
									<tr key={book._id}>
										<td>{book.name}</td>
										<td>{book.author}</td>
										<td>â‚¹{book.price}</td>
										<td>
											<button id={book._id}>Add to cart</button>
										</td>
									</tr>
								);
							})
						)}
					</tbody>
				</table>
			)}
		</>
	);
};
