import axios from "axios";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";
import { useDispatch, useSelector } from "react-redux";
import { cartDataFetchRequest, cartDataFetchSuccess } from "../redux/actions";

export const Cart = () => {
	const [cookies, setCookies] = useCookies("");
	const cartData = useSelector((store) => store.cartData);
	const loading = useSelector((store) => store.loading);
	const dispatch = useDispatch();

	useEffect(() => {
		if (cookies.loginToken !== undefined) {
			let books = [];
			axios
				.post("http://localhost:3001/cartFind", {
					user_id: jwtDecode(cookies.loginToken).user._id,
				})
				.then((res) => {
					books.push(res.data.book_ids);
					console.log(books);
					for (let i = 0; i < books.length; i++) {
						axios
							.post("http://localhost:3001", { _id: books[i] })
							.then((res) => {
								console.log(res, books[i]);
								// dispatch(cartDataFetchRequest());
								// dispatch(cartDataFetchSuccess(res.data));
							});
					}
					// .map((book) => {
					// 	axios
					// 		.post("http://localhost:3001", { book_id: book })
					// 		.then((res) => {
					// 			console.log(res, book);
					// 			// dispatch(cartDataFetchRequest());
					// 			// dispatch(cartDataFetchSuccess(res.data));
					// 		});
					// });
				});
		}
		if (cookies.loginToken === undefined) {
			dispatch(cartDataFetchRequest());
			dispatch(cartDataFetchSuccess([]));
		}
	}, [cookies, dispatch]);

	// console.log(cartData);

	return loading ? (
		<h3>Loading...</h3>
	) : (
		<table>
			<thead>
				<tr>
					<th>Book Name</th>
					<th>Author</th>
					<th>Price</th>
				</tr>
			</thead>
			{cartData.length === 0 ? (
				<h3>Empty Cart</h3>
			) : (
				cartData.map((cart) => {
					return (
						<tbody>
							<tr>
								<td>{cart.name}</td>
								<td>{cart.author}</td>
								<td>{cart.price}</td>
							</tr>
						</tbody>
					);
				})
			)}
		</table>
	);
};
