import axios from "axios";
import jwtDecode from "jwt-decode";
import { useEffect, useState } from "react";
import { useCookies } from "react-cookie";

export const Cart = () => {
	const [cartData, setCartData] = useState([]);
	const [cookies, setCookies] = useCookies("");

	useEffect(() => {
		console.log(cookies.loginToken);
		if (cookies.loginToken !== undefined) {
			axios
				.post("http://localhost:3001/cartFind", {
					user_id: jwtDecode(cookies.loginToken).user._id,
				})
				.then((res) => {
					console.log(res.data);
					let data = [];
					res.data.book_ids.map((book) => {
						axios
							.post("http://localhost:3001", { book_id: book })
							.then((res) => data.push(res.data));
						console.log(data);
						setCartData(data);
					});
				});
		} else {
			setCartData([]);
		}
	}, []);

	console.log(cartData);

	return (
		<div>
			{cartData.map((cart) => {
				return (
					<div>
						<p>{cart.name}</p>
						<p>{cart.author}</p>
						<p>{cart.price}</p>
					</div>
				);
			})}
		</div>
	);
};
